mapper.delete:
delete from org_class_students
where id in

  #{item.id,jdbcType=INTEGER}

mapper.update:
update org_class_students
set classId = #{classId,jdbcType=INTEGER},
  studentId = #{studentId,jdbcType=INTEGER},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from org_class_students
where 1 = 1

  and classId = #{classId,jdbcType=INTEGER}


  and studentId = #{studentId,jdbcType=INTEGER}


  and orderNo = #{orderNo,jdbcType=VARCHAR}


  and status = #{status,jdbcType=INTEGER}


  group by classId

select

from org_class_students
where 1 = 1

  and createTime >= #{startTime,jdbcType=VARCHAR}


  and createTime <= #{endTime,jdbcType=VARCHAR}


  and status = #{status,jdbcType=INTEGER}

select count(distinct(studentId))
from org_class_students
where status = 1

  and createTime >= #{startTime,jdbcType=VARCHAR}


  and createTime <= #{endTime,jdbcType=VARCHAR}

mapper.update:
update org_class_students
set status = #{status,jdbcType=INTEGER}
where orderNo = #{orderNo,jdbcType=VARCHAR}
