mapper.update:
update org_class_test
set status = #{classDate,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select

from org_class_test
where 1 = 1

  and classId = #{classId,jdbcType=INTEGER}


  and status = #{status,jdbcType=INTEGER}

order by classDate desc
limit #{start}, #{pageSize}

select count(*)
from org_class_test
where 1 = 1

  and classId = #{classId,jdbcType=INTEGER}


  and status = #{status,jdbcType=INTEGER}

order by classDate desc
